        align-items: center;
        justify-content: space-between;

        font-size: 350%;
        text-align: right;

        img {
            display:none;
            width:13vw;
        }

    }

    div.text {
        padding-left: theme.$base-padding;
        padding-right: theme.$base-padding;
        flex-grow: 2;
        margin:auto;
        margin-top:10px;
        width: 90%;
        line-height: 100%;
        // height:100%;
        display: flex;
        flex-direction: column;
        justify-content: flex-start;

        .nes-container {
            background-color: transparent;

            h3.title {
                background-color: theme.$bg-default;
                margin-top: -1.5rem !important;
                font-size: 75%;
                margin-bottom: 0px;
            }

            &.popout {
                width:115%;
                margin-left:-7%;
            }
        }
    }



    &.positive  {
        background: theme.$bg-good;

        div.gradient {
            background: theme.$gradient-good;
        }

        h1 {

            img {
                display: inline-block;
            }
            font-size: 350%;
            text-align: right;
        }

        // .nes-container

        div.buttons {background: theme.$bg-good-light;}
    }

    &.negative  {

        background: theme.$bg-bad;

        div.gradient {
            background: theme.$gradient-bad;
        }

        h1 {
            img {
                display: inline-block;
            }
            font-size: 350%;
            text-align: center;
        }

        div.buttons { background: theme.$bg-bad-light;}
    }

    &.endgame {
        h1 {
            img {
                display: inline-block;
            }
            font-size: 350%;
            text-align: center;
        }
    }


    div.gradient {
        display: block;
        background: theme.$gradient-default;
        height:8vh;
    }

    div.buttons {
        display: flex;
        flex-direction: column;
        padding: theme.$base-padding;
        background: theme.$bg-default-light;
    }

    button {
        margin-bottom: 4%;
        line-height: 90%;
        min-height: fit-content;
        -webkit-appearance: none;
    }
}

//Iphone 6 SE
@media screen and (max-width: 330px){ 
    section.screen {
        font-size: 110%;
        img.cover {
            height: 20vh;
        }
    }
}

//iPhone 8
@media screen and (min-width: 340px) and (max-height: 600px){ 

    section.screen {
        font-size: 130%;
        img.cover {
            height: 22vh;
        }
    }
}

//Desktop
@media screen and (min-width: 1024px){

    section.screen {
        flex-direction: row;
        justify-content: center;
        align-items: center;

        font-size: 200%;

        img.cover {
            margin-bottom: 20vh !important;
        }

        .text_wrapper {
            margin-bottom: 20vh !important;
        }

        button {
            height: fit-content;
        }
    }

    .screen-defaults {



         img.cover {
             position: absolute;
             left: 0px;
             top:0px;
         }

    }

    .text_wrapper.nes-container.is-rounded.is-dark{
        background-color: transparent;
    }

    section.screen div.text_wrapper {
        height: fit-content;
    }

    div.text{
        padding-bottom: 3%;
    }

    //flexbox - children layout
    section.screen img.cover,
    .text_wrapper {
        width:30%;
        max-width: 480px;
    }


    section.screen img.cover {
        margin: 0;
        object-fit: contain;
        margin-right:2.5%;
    }


    //response img size fix (positive, negative)
    section.screen.positive h1 img,
    section.screen.negative h1 img{
        width: 25%;
        max-width: 100px;
    }

    //endgame img snow
    section.screen.endgame h1 img {
        max-width: 100px;
    }

    //statusbar style
    section.statusbar {
        left: 10%;
        max-width: 80%;
        font-size: 150%;
    }

    section.statusbar ul {
        margin-right: 20px;
    }

    section.statusbar ul li img {
        width: 11px;
    }


    //button to the bottom
    section.screen div.buttons {
        position: absolute;
        bottom:0px;
        left: 0px;
        width: 100%;
        height:15%;
        display: flex;
        flex-direction: row;
        justify-content: center;
        padding-bottom: 0.5%;
    }

    div.buttons{
        height: 100%;
        min-height: 100px;
    }

    .gradient{
        position: absolute;
        width: 100%;
        left:0;
        bottom:15%;
    }

    .nes-btn {
        width: 38%;
        max-height: 150px;
        min-height: 70px;
    }

    .nes-btn + .nes-btn{
        margin-left: 4%;
    }

    .nes-container.is-dark {
        position: static !important;
    }

    .intro div.menu img#logo {
        width:50%;
        margin-top: -25%;
        margin-bottom: 3%;
    }

    .intro {
        height: min-content;      

        img.intro_title {
            width: 50%;
            margin-top: 40vh;
        }

        img.intro_graphics {
            width:70vw;
            margin-top: 10%;
        }

        div.menu {
            display: flex;
            flex-direction: column;
            justify-content: center;

            h1 {
                font-size: 200%;
            }

            button {
                width:80%;
            }
        }


    }  
